<script setup>
import { ref, onMounted, computed, watchEffect} from 'vue';
import GroupCard from './GroupCard.vue';
import swiperGroupCard from './swiperGroupCard.vue';
import { useGroupStore } from '@/stores/groups';
import { defineComponent, watch, nextTick } from 'vue';
import { useUserStore } from '@/stores/user.js';
import MultiSelect from 'primevue/multiselect';
import InputText from 'primevue/inputtext';
import svgPanZoom, { setMinZoom } from 'svg-pan-zoom';
import { defineProps } from 'vue';
import { useRoomsStore } from '@/stores/rooms.js';
import { usePopStore } from '@/stores/pop.js';
import { useFilteredGroupsStore } from '@/stores/filteredGroups';

import 'primeicons/primeicons.css';



const cards = ref(Array(5).fill(null));


const filteredGroupsStore = useFilteredGroupsStore();

const popupId = ref(null);
const svgElement = ref(null);
const groupTitle = ref('');
const groupTheme = ref('');
const groupDescription = ref('');
const creatingGroup = ref(false);
const roomsStore = useRoomsStore();
const fpInfo = ref(null);

const popStore = usePopStore();
const groupStore = useGroupStore();
const userStore = useUserStore();

const groups = groupStore.groups;
const filteredGroups = ref([]);

const createGroupIsActive = ref(false);

const fpInfoStyle = computed(() => {
  if (popStore.showPopup && helpText.value.main === "Press an area on the map to create a group in that room.") {
    return 'FpInfo-animated';
  } else {
    return '';
  }
});



function updateFilteredGroups(newFilteredGroups) {
  filteredGroups.value = newFilteredGroups;
}

function updateFilteredRoomIds(newFilteredRoomIds) {
  props.filteredRoomIds = newFilteredRoomIds;
  updateSvgElements(); 
}

const isDesktop = computed(() => {
  return window.innerWidth >= 768;
});

const helpText = computed(() => {
  return popStore.showPopup
    ? {
        main: "Press an area on the map to create a group in that room.",
        tipH: "Tips:",
        tip: "You can also zoom in and out on the map if needed",
      }
    : { main: "Press any area on the map to see the activity there. You can also use the searchbar",
        tipH: "Tips:", 
        tip: "Notice the groups on the left side updating accordingly" };
});



const props = defineProps({
  group: {
    type: Object,
    required: true,
  },
  isUsedInFloorPlan: {
    type: Boolean,
    default: false,
  },
  filteredRoomIds: {
    type: Array,
    default: () => [],
  },
});




const roomId = roomsStore.roomId;


onMounted(()  => {
  groupStore.listenToGroups();

  watch(
    () => filteredGroups.value,
    () => {
      updateSvgElements();
    },
    { deep: true }
  );

  watch(
    () => filteredGroupsStore.filteredRoomIds,
    () => {
      updateSvgElements();
    },
    { deep: true }
  );
});



function updateSvgElements() {
  const mappedGroups = new Map();
  if (svgElement.value) {
    const elements = svgElement.value.querySelectorAll('rect, path');
    const roomToGroup = new Map(groupStore.groups.map(group => [group.room, group]));

    elements.forEach((element) => {
      const group = roomToGroup.get(element.id);
      const roomIsFiltered = filteredGroupsStore.filteredRoomIds.includes(element.id);

      if (group) {
        mappedGroups.set(group, element);
        if (isUserInGroup(group)) {
          element.style.fill = 'url(#green-gradient)';
         } else if (filteredGroupsStore.filteredRoomIds.length === 0 || roomIsFiltered) {
          element.style.fill = 'url(#teal-gradient)';
        } else {
          element.style.fill = ''; 
        }
        element.style.opacity = '0.6';
        element.classList.add('breathing-effect');
      }

      element.addEventListener('click', () => {
        if (!popStore.showPopup) {
          roomsStore.setRoomId(element.id);
          return;
        }
        showPopup(element.id);
        roomsStore.setRoomId(element.id);
      });
    });
    
  }

}


function highlightGroups(roomIds, duration = 3500) {
  const elements = svgElement.value.querySelectorAll('rect:not(.exclude), path:not(.exclude)');
  elements.forEach((element) => {
    if (element.id) {
      const originalFill = element.style.fill;
      element.style.fill = 'rgba(255, 165, 0, 0.6)';
      setTimeout(() => {
        element.style.fill = originalFill;
      }, duration);
    } else {
      element.style.fill = '';
    }
  });
}


onMounted(() => {
  if (svgElement.value) {
    const instance = svgPanZoom(svgElement.value, {
      zoomEnabled: true,
      controlIconsEnabled: true,
      minZoom: 1,
      zoomScaleSensitivity: 0.5,
    });
    let initialZoom = instance.getZoom();
    let initialPosition = instance.getPan();
    let currentPosition = initialPosition;
    instance.setOnZoom((newZoom) => {
      if (newZoom > initialZoom) {
        currentPosition = instance.getPan();
      }
      initialZoom = newZoom;
      instance.pan(currentPosition);
    });
  }
});



  watch(() => popStore.showPopup, (newValue, oldValue) => {
if (!newValue) {
hidePopup();
}});


watchEffect(() => {
  const roomIds = filteredGroups.value.map(group => group.room);
});



function isUserInGroup(group) {
  return group.participants.includes(userStore.username);
}


async function createGroup(popupId) {
  if (!groupTitle.value || !groupTheme.value || !groupDescription.value) {
    alert('Please fill in all required fields.');
    return;
  };
  const newGroup = {
    room: popupId,
    title: groupTitle.value,
    themes: groupTheme.value,
    description: groupDescription.value,
    participants: [userStore.username],
  };
  await groupStore.createGroup(newGroup);
  hidePopup();
}

function hidePopup() {
  resetForm();
  popupId.value = null;
  if (popStore.showPopup) {
    popStore.setShowPopup(false);
  }
}



function showPopup(id) {
  
  popupId.value = id;
  
}

function debounce(func, wait) {
  let timeout;
  return function(...args) {
    const context = this;
    clearTimeout(timeout);
    timeout = setTimeout(() => {
      func.apply(context, args);
    }, wait);
  };
}
const groupThemes = [
  'General',
  'Coding',
  'Gaming',
  'Lunch',
  'Assignment work',
  'Reading',
  'Sports',
  'Exam-practice'
];



function resetForm() {
  groupTitle.value = '';
  groupTheme.value = '';
  groupDescription.value = '';
}


defineComponent({
  components: {
    GroupCard,
    swiperGroupCard,
    
  },
});


</script>

